import React from "react";
import styled from "styled-components";

const Panel = styled.div `
  border: 1px solid black;
  margin: 0.5rem;
  padding: 0.5rem;
`;

const PanelPagina = () => {
  const [formData, setFormData] = React.useState([
    {
      name: 'numPagines',
      number: 1,
    },
    {
      name: 'numIdiomes',
      number: 1,
    }
    ]
  )

  function handleChange(event){
    const {name, value} = event.target
    setFormData(prevFormData => {
        const newFormData = []
        for (let i = 0; i < prevFormData.length; i++) {
          const currentInput = prevFormData[i]
          if (currentInput.name === name) {
            const updatetInput = {
              ...currentInput,
              name: value,
            }
            newFormData.push(updatetInput)
          } else {
            newFormData.push(currentInput)
          }
        }
        return newFormData
    })
  }

  return (
    <Panel>
      <input
        name='numPagines'
        onChange={handleChange}
        placeholder='Número de pàgines'
        type='number'
        value={formData.numPagines}
      />
      <input
        name='numIdiomes'
        onChange={handleChange}
        placeholder='Número de idiomas'
        type='number'
        value={formData.numIdiomes}
      />
    </Panel>
  )
}

export default PanelPagina
