import './App.css';
import React from "react";

function App() {
  const title = '¿Qué quieres hacer?';
  const data = [
    {
      name: 'web',
      price: 500,
      product: 'Una pàgina web',
    },
    {
      name: 'seo',
      price: 300,
      product: 'Una campanya SEO',
    },
    {
      name: 'ads',
      price: 200,
      product: 'Una campanya de Google Ads',
    },
  ];
  const [formData, setFormData] = React.useState({
    web: false,
    seo: false,
    ads: false,
  });
  const total = 0;

  function handleChange(event) {
    const {name, value, type, checked} = event.target
    setFormData(prevFormData => {
      return {
        ...prevFormData,
        [name]: type === 'checkbox' ? checked : value
      }
    })
  }

  return (
    <div className="App">
      <header className="App-header">
        <h6>{title}</h6>

        <input
          checked={formData.web}
          id={data[0].name}
          name={data[0].name}
          type='checkbox'
          onChange={handleChange}
        />
        <label htmlFor={data[0].name}>
          {data[0].product} ({data[0].price}€)
        </label>

        <input
          checked={formData.seo}
          id={data[1].name}
          name={data[1].name}
          type='checkbox'
          onChange={handleChange}
        />
        <label htmlFor={data[1].name}>
          {data[1].product} ({data[1].price}€)
        </label>

        <input
          checked={formData.ads}
          id={data[2].name}
          name={data[2].name}
          type='checkbox'
          onChange={handleChange}
        />
        <label htmlFor={data[2].name}>
          {data[1].product} ({data[1].price}€)
        </label>

        <p>Preu: {total}€</p>
      </header>
    </div>
  );
}

export default App;
