import React, { Component } from 'react';
import styled from "styled-components";

const DialogContainer = styled.div `
  background-color: #eee;
  border-radius: 8px;
  bottom: 0;
  display: flex;
  flex-direction: column;
  left: 50%;
  margin: 0 auto;
  max-width: 100%;
  padding: 10px 20px 40px;
  position: fixed;
  transform: translate(-50%,-50%);
  width: 500px;
  z-index: 999;
`;

const DialogCloseButtonStyles = styled.div `
  margin-bottom: 15px;
  padding: 3px 8px;
  cursor: pointer;
  border-radius: 50%;
  border: none;
  width: 30px;
  height: 30px;
  font-weight: bold;
  align-self: flex-end;
`;


class Dialog extends Component {
  render() {
    let dialog = (
      <DialogContainer>
        <DialogCloseButtonStyles onClick={this.props.onClose}>x</DialogCloseButtonStyles>

        <div>{this.props.children}</div>
      </DialogContainer>
    );

    if (! this.props.isOpen) {
      dialog = null;
    }
    return (
      <div>
        {dialog}
      </div>
    );
  }
}

export default Dialog;
