import React from "react";
import {Panel, RowPanel, RowPanelTitle} from "./PanelStyled";
import Modal from "../Modal";

const PanelComponent = ({data, onChange, decrement, increment}) => {
  const [visibleModal, setVisibleModal] = React.useState(false);

  return (
    <Panel>
      {
        data.map(input =>
          <RowPanel key={input.name}>
            <RowPanelTitle>
              <label htmlFor={input.name}>
                {input.label}
              </label>
            </RowPanelTitle>
            <button disabled={input.quantity===0} onClick={() => decrement(input.name)}>-</button>
            <input
              id={input.name}
              name={input.name}
              onChange={onChange}
              type='number'
              value={input.quantity}
            />
            <button onClick={() => increment(input.name)}>+</button>
            <button onClick={setVisibleModal(true)}>info</button>
            <Modal visible={visibleModal} children={input.modal} handleClick={setVisibleModal(false)} />
          </RowPanel>
        )
      }
    </Panel>
  )
}

export default PanelComponent
