import React from "react";
import styled from "styled-components";

const Panel = styled.div `
  border: 1px solid black;
  margin: 0.5rem;
  padding: 0.5rem;
`;

const PanelPagina = () => {
  const [formData, setFormData] = React.useState([
    {
      label: 'Número de pàgines',
      name: 'numPagines',
      number: 1,
    },
    {
      label: 'Número de idiomes',
      name: 'numIdiomes',
      number: 1,
    }
    ]
  )

  function handleChange(event){
    const {name, value} = event.target
    setFormData(prevFormData => {
        const newFormData = []
        for (let i = 0; i < prevFormData.length; i++) {
          const currentInput = prevFormData[i]
          if (currentInput.name === name) {
            const updatetInput = {
              ...currentInput,
              name: value,
            }
            newFormData.push(updatetInput)
          } else {
            newFormData.push(currentInput)
          }
        }
        return newFormData
    })
  }

  return (
    <Panel>
      {
        formData.map(input =>
          <div key={input.name}>
            <label htmlFor={input.name}>
              {input.label}
            </label>
            <input

              id={input.name}
              name={input.name}
              onChange={handleChange}
              placeholder={input.label}
              type='number'
              value={input.number}
            />
          </div>
        )
      }
    </Panel>
  )
}

export default PanelPagina
