import React from "react";

function App() {
  const title = '¿Qué quieres hacer?';

  const [formData, setFormData] = React.useState([
    {
      name: 'web',
      price: 500,
      product: 'Una pàgina web',
      selected: false,
    },
    {
      name: 'seo',
      price: 300,
      product: 'Una campanya SEO',
      selected: false,
    },
    {
      name: 'ads',
      price: 200,
      product: 'Una campanya de Google Ads',
      selected: false,
    },
  ]);
  const total = 0;

  function handleChange(event) {
    const {name, value, type, checked} = event.target
    setFormData(prevFormData => {
      const newFormData = []
      for (let i = 0 ; i < prevFormData.length; i++){
        const currentItem = prevFormData[i]
        if (currentItem.name === name) {
          const updatedItem = {
            ...currentItem,
            [name]: type === 'checkbox' ? checked : value
          }
          newFormData.push(updatedItem)
        }
        else {
          newFormData.push(currentItem)
        }
      }
    })
  }

  return (
    <div className="App">
      <header className="App-header">
        <h6>{title}</h6>
        {formData.map(item=>
          <div>
            {item.name}{item.price}{item.product}
            <input
              checked={item.selected}
              id={item.name}
              name={item.name}
              type='checkbox'
            />
            <label htmlFor={item.name}>
              {item.name}
            </label>
          </div>
        )}
        <p>Preu: {total}€</p>
      </header>
    </div>
  );
}

export default App;
