import React from "react";
import {Panel, RowPanel, RowPanelTitle} from "./PanelStyled";
import Dialog from "../Dialog/Dialog";

state = {
  isOpen: false
}

const PanelComponent = ({data, onChange, decrement, increment}) => {

  return (
    <Panel>
      {
        data.map(input =>
          <RowPanel key={input.name}>
            <RowPanelTitle>
              <label htmlFor={input.name}>
                {input.label}
              </label>
            </RowPanelTitle>
            <button disabled={input.quantity===0} onClick={() => decrement(input.name)}>-</button>
            <input
              id={input.name}
              name={input.name}
              onChange={onChange}
              type='number'
              value={input.quantity}
            />
            <button onClick={() => increment(input.name)}>+</button>
            <button onClick={(e) => this.setState({ isOpen: true })}>Open Dialog</button>

            <Dialog isOpen={this.state.isOpen} onClose={(e) => this.setState({ isOpen: false })}>
              Lorem ipsum dolor sit amet consectetur, adipisicing elit. Iste a ipsam repellendus commodi ad, fugit id magnam inventore laudantium autem delectus praesentium incidunt debitis, numquam dicta eveniet obcaecati, itaque quidem?
            </Dialog>
          </RowPanel>
        )
      }
    </Panel>
  )
}

export default PanelComponent
